import './App.css'
// Definimos variables de estado que cambian
import { useState } from 'react'

// Le damos una funci贸n que genera n煤mero aleatorio
const randomNumber = () => Math.trunc(Math.random() * 20) + 1

// LA funci贸n App
function App() {
  // Devuelve dos valores, variable y funcion que puede modificar la variable
  const [score, setScore] = useState(10)
  const [secretNumber, setSecretNumber] = useState(randomNumber())
  const handleCheck = () => {
    setScore(score - 1)
  }

  return (
    // Elemento vac铆o para que recoja un solo elemento. React solo puede devolver un elemento.
    <>
      <header>
        <h1>Guess My Number!</h1>
        <p className="between">(Between 1 and 20)</p>
        <button className="btn again">Again!</button>
        <div className="number">?</div>
      </header>
      <main>
        <section className="left">
          <input type="number" className="guess" />
          <button className="btn check" onClick={handleCheck}>
            Check!
          </button>
        </section>
        <section className="right">
          <p className="message">Start guessing...</p>
          <p className="label-score">
             Score: <span className="score">{score}</span>
          </p>
          <p className="label-highscore">
             Highscore: <span className="highscore">0</span>
          </p>
        </section>
      </main>
    </>
  )
}

// Exportamos el App
export default App
