import { useState, useRef } from 'react'
import MostrarMensaje from './MostrarMensaje'

//Otro react hook. useRef

// Le damos una funciÃ³n que genera nÃºmero aleatorio
const randomNumber = () => Math.trunc(Math.random() * 20) + 1
