  const handleCheck = () => {
    // Tomamos el numero que introduce el usuario (ver arriba)
    setGuessNumber(Number(inputRef.current.value))
    // Si no hay numero, no hace nada.
    if (!guessNumber) return
    // Comparando nÃºmeros
    if (guessNumber === secretNumber) {
      //actualizamos mensaje y color de fondo.
    } else {
      //actualizar numeros
      if (score > 1) {
        // Por cada check, bajamos un punto si no aciertas.
        // Obtener el numero secreto y comprobar si coincide con el nÃºmero introducido
        setScore(score - 1)
      } else {
        setScore(0)
      }
    }
  }

  // FunciÃ³n que resetea el juego. Y poner el valor del score y genera nuevo numero.
  const handleReset = () => {
    setScore(INITIAL_SCORE)
    setSecretNumber(randomNumber())
    setGuessNumber('')
  }

  // Esta funcion devuelve el HTML al navegador.

  return (
    // Elemento vacÃ­o para que recoja un solo elemento. React solo puede devolver un elemento.
    <>
      <header>
        <h1>Guess My Number!</h1>
        <p className="between">(Between 1 and 20)</p>
